import React, { useState, useEffect, useRef } from 'react';
import { useParams } from 'react-router-dom';
import '../styles/Messages.css';
import ExchangeStatus from './ExchangeStatus';
import ExchangeActions from './ExchangeActions';
import 'react-toastify/dist/ReactToastify.css';

const Messages = ({ currentUserId }) => {
  const { itemId, receiverId } = useParams();
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [showExchangePrompt, setShowExchangePrompt] = useState(false);
  const messagesEndRef = useRef(null);
  const [initiatorId, setInitiatorId] = useState(null);
  const [exchangeStatus, setExchangeStatus] = useState('pending');

  useEffect(() => {
    const fetchMessages = async () => {
      try {
        const res = await fetch(`http://localhost:5000/api/messages/${itemId}/${currentUserId}/${receiverId}`);
        if (res.ok) {
          const data = await res.json();
          setMessages(data);
          if (data.length > 0) {
            setInitiatorId(data[0].sender_id);
          }
        } else {
          console.error('Failed to fetch messages');
        }
      } catch (err) {
        console.error('Error fetching messages:', err);
      }
    };

    const fetchExchangeStatus = async () => {
      try {
        const res = await fetch(`http://localhost:5000/api/exchange/status/${itemId}`);
        if (res.ok) {
          const data = await res.json();
          setExchangeStatus(data.status);
        }
      } catch (err) {
        console.error('Error fetching exchange status:', err);
      }
    };

    fetchMessages();
    fetchExchangeStatus();

    const interval = setInterval(() => {
      fetchMessages();
      fetchExchangeStatus();
    }, 3000);

    return () => clearInterval(interval);
  }, [itemId, currentUserId, receiverId]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSend = async () => {
    if (newMessage.trim() === '') return;

    try {
      const res = await fetch('http://localhost:5000/api/messages', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          senderId: currentUserId,
          receiverId,
          itemId,
          content: newMessage,
        }),
      });

      if (res.ok) {
        setMessages(prev => [
          ...prev,
          {
            sender_id: currentUserId,
            receiver_id: receiverId,
            item_id: itemId,
            content: newMessage,
            timestamp: new Date().toISOString(),
          },
        ]);
        setNewMessage('');
        scrollToBottom();
      } else {
        console.error('Failed to send message');
      }
    } catch (err) {
      console.error('Error sending message:', err);
    }
  };

  const handleExchangePrompt = async (response) => {
    if (
      (currentUserId === initiatorId && exchangeStatus !== 'pending') ||
      (currentUserId !== initiatorId && exchangeStatus !== 'initiator_agreed')
    ) {
      toast.success(`Status updated: ${updateRes.data.status}`);
      return;
    }

    const message = response === 'yes'
      ? '✅ I agree to exchange this item.'
      : '❌ I do not want to exchange this item.';

    try {
      await fetch('http://localhost:5000/api/messages', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          senderId: currentUserId,
          receiverId,
          itemId,
          content: message,
        }),
      });

      await fetch('http://localhost:5000/api/exchange/response', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          itemId,
          requesterId: currentUserId,
          response,
        }),
      });

      setMessages(prev => [
        ...prev,
        {
          sender_id: currentUserId,
          receiver_id: receiverId,
          item_id: itemId,
          content: message,
          timestamp: new Date().toISOString(),
        },
      ]);

      setShowExchangePrompt(false);
      scrollToBottom();

      const statusRes = await fetch(`http://localhost:5000/api/exchange/status/${itemId}`);
      if (statusRes.ok) {
        const statusData = await statusRes.json();
        setExchangeStatus(statusData.status);
      }
    } catch (err) {
      console.error('Error handling exchange prompt:', err);
    }
  };

  return (
    <div className="messages-container">
      <h2>Messages</h2>
      <ExchangeStatus
        itemId={itemId}
        currentUserId={currentUserId}
        senderId={currentUserId}
        receiverId={receiverId}
      />

      <div className="messages-list">
        {messages.map((msg, index) => (
          <div key={index} className={`message ${msg.sender_id === currentUserId ? 'sent' : 'received'}`}>
            <strong>{msg.sender_id === currentUserId ? 'You' : 'Them'}:</strong> {msg.content}
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>

      <div className="message-input">
        <input
          type="text"
          placeholder="Type your message..."
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
        />
        <button onClick={handleSend}>Send</button>
      </div>

      {(
  (currentUserId === initiatorId && exchangeStatus === 'pending') ||
  (currentUserId !== initiatorId && exchangeStatus === 'Request for exchange')
) && (
  <div className="exchange-actions-wrapper">
    <ExchangeActions
      itemId={itemId}
      requesterId={currentUserId}
      userRole={currentUserId === initiatorId ? 'initiator' : 'receiver'}
    />
    <ToastContainer position="top-right" autoClose={3000} hideProgressBar={false} />
  </div>
)}
    </div>
  );
};

export default Messages;
