import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import MapPicker from './MapPicker';
import '../styles/ItemList.css';

const ItemList = () => {
  const [items, setItems] = useState([]);
  const [recommendations, setRecommendations] = useState([]);
  const [full_name, setfull_name] = useState('');
  const [description, setDescription] = useState('');
  const [exchangeCondition, setExchangeCondition] = useState('');
  const [category, setCategory] = useState('electronics'); // New state
  const [location, setLocation] = useState(null);
  const [image, setImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [userId, setUserId] = useState(null);
  const [messages, setMessages] = useState({ type: '', text: '' });
  const [showRecentOnly, setShowRecentOnly] = useState(false);
  const [interests, setInterests] =useState('');

  const navigate = useNavigate();

  useEffect(() => {
  const token = localStorage.getItem('token');
  if (token) {
    try {
      const decoded = jwtDecode(token);
      const interests = decoded.interests|| decoded.email || decoded.full_name;
      setInterests(decoded.interests);
      fetchRecommendations(interests);
    } catch (err) {
      console.error('Invalid token:', err);
    }
  }

  fetchItems(); // Load all items
}, []);

  const fetchItems = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/items');
      const result = await response.json();
      setItems(result);
    } catch (error) {
      console.error('Error fetching items:', error);
    }
  };



const fetchRecommendations = async (interests) => {
  try {
    const response = await fetch(`http://localhost:5000/recommend?identifier=${encodeURIComponent(interests)}`);
    const result = await response.json();

    if (!response.ok) {
      console.warn('Recommendation error:', result?.error || 'Unknown error');
      return;
    }

    if (Array.isArray(result.items)) {
      setRecommendations(result.items);
      if (result.fallback) {
        setFallbackMessage(result.message || 'Showing fallback recommendations.');
      } else {
        setFallbackMessage('');
      }
    } else {
      console.warn('No recommendations returned.');
    }
  } catch (error) {
    console.error('Error fetching recommendations:', error.message || error);
  }
};



  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    } else {
      setImagePreview(null);
    }
  };

  const enhanceLocation = async (location) => {
    try {
      const response = await fetch('http://localhost:5000/nearby', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(location),
      });
      const data = await response.json();
      return data.items;
    } catch (err) {
      console.error('Location enhancement failed:', err);
      return [];
    }
  };

const handleSubmit = async (e) => {
  e.preventDefault();
  const token = localStorage.getItem('token');
  if (!token || !userId || !location) return;

  const nearbyItems = await enhanceLocation(location);

  const formDataToSend = new FormData();
  formDataToSend.append('title', name);
  formDataToSend.append('description', description);
  formDataToSend.append('location', JSON.stringify(location));
  formDataToSend.append('image', image);
  formDataToSend.append('exchangeCondition', exchangeCondition);
  formDataToSend.append('category', category);

  try {
    const response = await fetch('http://localhost:5000/api/items', {
      method: 'POST',
      headers: {
        Authorization: `Bearer ${token}`,
      },
      body: formDataToSend,
    });

    const result = await response.json();

    if (response.ok) {
        await fetch('http://localhost:5000/log_item', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            user_id: userId,
            item_id: result.item_id,
            category,
            location,
            description,
            title: name,
            exchange_condition: exchangeCondition,
            poster_name: identifier,
          }),
        });

setMessages({ type: 'success', text: result.message || 'Item posted successfully!' });
setName('');
setDescription('');
setExchangeCondition('');
setCategory('electronics');
setLocation(null);
setImage(null);
setImagePreview(null);
if (result.item) {
fetchItems();
}
    } 
  } catch (error) {
    setMessages({ type: 'error', text: 'Item posted successfully!' });
  }
};

  const toggleView = () => {
    setShowRecentOnly(!showRecentOnly);
  };

  const displayedItems = showRecentOnly ? items.slice(0, 5) : [...items].sort((a, b) => a.category.localeCompare(b.category));

  return (
    <div className="item-list-container">
      <h2>Available Items</h2>

      {messages.text && (
        <div className={`messages ${messages.type}`}>
          {messages.text}
        </div>
      )}

      {userId && (
        <form onSubmit={handleSubmit} className="item-form" encType="multipart/form-data">
          <input
            type="text"
            placeholder="Item Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
          <textarea
            placeholder="Item Description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
          />
          <input
            type="text"
            placeholder="Exchange Conditions"
            value={exchangeCondition}
            onChange={(e) => setExchangeCondition(e.target.value)}
            required
          />
          <select value={category} onChange={(e) => setCategory(e.target.value)} required>
            <option value="electronics">Electronics</option>
            <option value="clothes">Clothes</option>
            <option value="textbook">Textbook</option>
          </select>
          <label>Select Location on Map:</label>
          <MapPicker setLocation={setLocation} />
          <input
            type="file"
            accept="image/*"
            onChange={handleImageChange}
            required
          />
          {imagePreview && (
            <img src={imagePreview} alt="Preview" className="image-preview" />
          )}
          <button type="submit">Post Item</button>
        </form>
      )}

      <button onClick={toggleView} className="toggle-button">
        {showRecentOnly ? 'Show All Items' : 'Show Recent Items'}
      </button>

      <ul className="item-list">
        {displayedItems.map((item) => {
          let loc = null;
          try {
            loc = JSON.parse(item.location);
          } catch (e) {
            loc = null;
          }

          return (
            <li key={item.id} className="item-card">
              <h3>{item.title}</h3>
              <p>{item.description}</p>
              <p><strong>Exchange Conditions:</strong> {item.exchange_condition}</p>
              <p><strong>Category:</strong> {item.category}</p>
              <p><strong>Posted by:</strong> {item.poster_name}</p>
              <img
                src={`http://localhost:5000/api/items/${item.id}/image`}
                alt={item.title}
                className="item-image"
              />
              {loc && (
                <div className="item-map">
                  <iframe
                    width="100%"
                    height="200"
                    frameBorder="0"
                    style={{ border: 0 }}
                    src={`https://www.google.com/maps?q=${loc.lat},${loc.lng}&hl=es;z=14&output=embed`}
                    allowFullScreen
                    title="Item Location"
                  ></iframe>
                </div>
              )}
              {userId !== item.user_id && (
                <button
                  className="messages-button"
                  onClick={() => navigate(`/messages/${item.id}/${item.user_id}`)}
                >
                  Message Re-user
                </button>
              )}
            </li>
          );
        })}
      </ul>

     {recommendations.length > 0 && (
        <div className="recommendations">
          <h3>Recommended for You</h3>
          <ul className="item-list">
            {recommendations.map((item) => (
              <li key={item.item_id} className="item-card">
                <h4>{item.title}</h4>
                <p>{item.description}</p>
                <p><strong>Exchange Conditions:</strong> {item.exchange_condition}</p>
                <p><strong>Category:</strong> {item.category}</p>
                <p><strong>Posted by:</strong> {item.poster_name}</p>
                <img
                  src={`http://localhost:5000/api/items/${item.item_id}/image`}
                  alt={item.title}
                  className="item-image"
                />
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default ItemList;
