import React, { useEffect, useState } from 'react';
import '../styles/ItemList.css';
import { getAuth } from 'firebase/auth';
import app from '../firebase/firebaseConfig';

const ItemList = () => {
  const [items, setItems] = useState([]);
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [exchangeConditions, setExchangeConditions] = useState('');
  const [location, setLocation] = useState('');
  const [image, setImage] = useState(null);
  const [user, setUser] = useState(null);

  const auth = getAuth(app);

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged((currentUser) => {
      setUser(currentUser);
    });
    fetchItems();
    return () => unsubscribe();
  }, []);

  const fetchItems = async () => {
    try {
      const response = await fetch('/api/items');
      const data = await response.json();
      setItems(data);
    } catch (error) {
      console.error('Error fetching items:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!user) return;

    const formData = new FormData();
    formData.append('name', name);
    formData.append('description', description);
    formData.append('exchangeConditions', exchangeConditions);
    formData.append('location', location);
    formData.append('image', image);
    formData.append('userId', user.uid);

    try {
      const response = await fetch('/api/items', {
        method: 'POST',
        body: formData,
      });

      if (response.ok) {
        setName('');
        setDescription('');
        setExchangeConditions('');
        setLocation('');
        setImage(null);
        fetchItems();
      }
    } catch (error) {
      console.error('Error posting item:', error);
    }
  };

  return (
    <div className="item-list-container">
      <h2>Available Items</h2>

      {user && (
        <form onSubmit={handleSubmit} className="item-form" encType="multipart/form-data">
          <input
            type="text"
            placeholder="Item Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
          <textarea
            placeholder="Item Description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
          />
          <input
            type="text"
            placeholder="Exchange Conditions"
            value={exchangeConditions}
            onChange={(e) => setExchangeConditions(e.target.value)}
            required
          />
          <input
            type="text"
            placeholder="Location"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
            required
          />
          <input
            type="file"
            accept="image/*"
            onChange={(e) => setImage(e.target.files[0])}
            required
          />
          <button type="submit">Post Item</button>
        </form>
      )}

      <ul className="item-list">
        {items.map((item) => (
          <li key={item.id} className="item-card">
            <h3>{item.name}</h3>
            <p>{item.description}</p>
            <p><strong>Exchange Conditions:</strong> {item.exchangeConditions}</p>
            <p><strong>Location:</strong> {item.location}</p>
            {item.imageUrl && <img src={item.imageUrl} alt={item.name} className="item-image" />}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ItemList;
