import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import MapPicker from './MapPicker'; // Make sure this file exists
import '../styles/ItemList.css';

const ItemList = () => {
  const [items, setItems] = useState([]);
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [exchangeCondition, setExchangeCondition] = useState('');
  const [location, setLocation] = useState(null); // { lat, lng }
  const [image, setImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [userId, setUserId] = useState(null);
  const [messages, setMessages] = useState({ type: '', text: '' });
  const [showRecentOnly, setShowRecentOnly] = useState(false);

  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const decoded = jwtDecode(token);
        setUserId(decoded.id);
      } catch (err) {
        console.error('Invalid token:', err);
      }
    }

    const fetchItems = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/items');
        const result = await response.json();
        setItems(result);
      } catch (error) {
        console.error('Error fetching items:', error);
      }
    };

    fetchItems();
  }, []);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    } else {
      setImagePreview(null);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token || !userId || !location) return;

    const formData = new FormData();
    formData.append('title', name);
    formData.append('description', description);
    formData.append('location', JSON.stringify(location));
    formData.append('image', image);
    formData.append('exchangeCondition', exchangeCondition);
    formData.append('userId', userId);

    try {
      const response = await fetch('http://localhost:5000/api/items', {
        method: 'POST',
        headers: {
          Authorization: `Bearer ${token}`,
        },
        body: formData,
      });

      const result = await response.json();

      if (response.ok) {
        setMessages({ type: 'success', text: result.messages || 'Item posted successfully!' });
        setName('');
        setDescription('');
        setExchangeCondition('');
        setLocation(null);
        setImage(null);
        setImagePreview(null);

        const refreshedItems = await fetch('http://localhost:5000/api/items');
        const itemsData = await refreshedItems.json();
        setItems(itemsData);
      } else {
        setMessages({ type: 'error', text: result.messages || 'Failed to post item.' });
      }
    } catch (error) {
      setMessages({ type: 'error', text: 'Network error. Please try again later.' });
    }
  };

  const toggleView = () => {
    setShowRecentOnly(!showRecentOnly);
  };

  const displayedItems = showRecentOnly ? items.slice(0, 5) : items;

  return (
    <div className="item-list-container">
      <h2>Available Items</h2>

      {messages.text && (
        <div className={`messages ${messages.type}`}>
          {messages.text}
        </div>
      )}

      {userId && (
        <form onSubmit={handleSubmit} className="item-form" encType="multipart/form-data">
          <input
            type="text"
            placeholder="Item Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
          <textarea
            placeholder="Item Description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
          />
          <input
            type="text"
            placeholder="Exchange Conditions"
            value={exchangeCondition}
            onChange={(e) => setExchangeCondition(e.target.value)}
            required
          />
          <label>Select Location on Map:</label>
          <MapPicker setLocation={setLocation} />
          <input
            type="file"
            accept="image/*"
            onChange={handleImageChange}
            required
          />
          {imagePreview && (
            <img src={imagePreview} alt="Preview" className="image-preview" />
          )}
          <button type="submit">Post Item</button>
        </form>
      )}

      <button onClick={toggleView} className="toggle-button">
        {showRecentOnly ? 'Show All Items' : 'Show Recent Items'}
      </button>

      <ul className="item-list">
        {displayedItems.map((item) => {
          let loc = null;
          try {
            loc = JSON.parse(item.location);
          } catch (e) {
            loc = null;
          }

          return (
            <li key={item.id} className="item-card">
              <h3>{item.title}</h3>
              <p>{item.description}</p>
              <p><strong>Exchange Conditions:</strong> {item.exchange_condition}</p>
              <p><strong>Posted by:</strong> {item.poster_name}</p>
              <img
                src={`http://localhost:5000/api/items/${item.id}/image`}
                alt={item.title}
                className="item-image"
              />
              {loc && (
                <div className="item-map">
                  <iframe
                    width="100%"
                    height="200"
                    frameBorder="0"
                    style={{ border: 0 }}
                    src={`https://www.google.com/maps?q=${loc.lat},${loc.lng}&hl=es;z=14&output=embed`}
                    allowFullScreen
                    title="Item Location"
                  ></iframe>
                </div>
              )}
              {userId !== item.user_id && (
                <button
                  className="messages-button"
                  onClick={() => navigate(`/messages/${item.id}/${item.user_id}`)}
                >
                  Messages Re-user
                </button>
              )}
            </li>
          );
        })}
      </ul>
    </div>
  );
};

export default ItemList;
