import React, { useState } from 'react';
import '../styles/MapView.css';

const MapView = () => {
  const [latitude, setLatitude] = useState('');
  const [longitude, setLongitude] = useState('');
  const [nearbyItems, setNearbyItems] = useState([]);
  const [error, setError] = useState('');
  
  // Load Google Maps script
  useEffect(() => {
    const loadMapScript = () => {
      const existingScript = document.getElementById('googleMaps');

      if (!existingScript) {
        const script = document.createElement('script');
        script.src =
          'https://maps.googleapis.com/maps/api/js?key=AIzaSyCIho9p8rGILl7OsjimbOMjsEn0SaaxrQE';
        script.id = 'googleMaps';
        script.async = true;
        script.defer = true;
        script.onload = () => setMapLoaded(true);
        document.head.appendChild(script);
      } else {
        setMapLoaded(true);
      }
    };

    loadMapScript();
  }, []);

  const handleLocationSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setNearbyItems([]);

    if (!latitude || !longitude) {
      setError('Please enter both latitude and longitude.');
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/nearby', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ latitude: parseFloat(latitude), longitude: parseFloat(longitude) })
      });
      const result = await response.json();
      if (response.ok) {
        setNearbyItems(result.items || []);
      } else {
        setError(result.error || 'Failed to fetch nearby items.');
      }
    } catch (err) {
      setError('Network error. Please try again later.');
    }
  };

  return (
    <div className="map-view-container">
      <h2>Find Nearby Items</h2>
      <form onSubmit={handleLocationSubmit} className="location-form">
        <input
          type="text"
          placeholder="Latitude"
          value={latitude}
          onChange={(e) => setLatitude(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Longitude"
          value={longitude}
          onChange={(e) => setLongitude(e.target.value)}
          required
        />
        <button type="submit">Search Nearby</button>
      </form>

      {error && <div className="error-message">{error}</div>}

      {nearbyItems.length > 0 && (
        <div className="nearby-results">
          <h3>Nearby Items</h3>
          <ul>
            {nearbyItems.map((itemId, index) => (
              <li key={index}>Item ID: {itemId}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default MapView;
