import React from 'react';
import { useNavigate } from 'react-router-dom';
import './ItemList.css';

function ItemList({ displayedItems, userId, recommendedItems = [] }) {
  const navigate = useNavigate();

  return (
    <div className="items-page">
      {/* LEFT: Recommendations */}
      <aside className="recommendations-panel sticky">
        <h2>Recommended for You</h2>

        {recommendedItems.length === 0 ? (
          <div className="recommendation-card">
            <h3>VIDA 4G/LTE wireless router</h3>
            <p>These devices provide internet access for multiple devices to use.</p>
          </div>
        ) : (
          recommendedItems.map((rec) => (
            <div key={rec.id} className="recommendation-card">
              <h3>{rec.title}</h3>
              {rec.description && <p>{rec.description}</p>}
            </div>
          ))
        )}
      </aside>

      {/* RIGHT: Your current list */}
      <main className="content-panel">
        <div className="item-list-container">
          <ul className="item-list">
            {displayedItems.map((item, index) => {
              let loc = null;
              try {
                loc = JSON.parse(item.location);
              } catch (e) {
                loc = null;
              }

              return (
                <li key={item.id ?? index} className="item-card">
                  {/* If you have images, render here; otherwise omit */}
                  {item.image_url && (
                    <img
                      className="item-image"
                      src={item.image_url}
                      alt={item.title ?? 'Item'}
                    />
                  )}

                  <div className="item-content">
                    <h3>{item.title}</h3>
                    <p>
                      {item.description}
                      <br />
                      <strong>Exchange Conditions:</strong> {item.exchange_condition}
                      <br />
                      <strong>Posted by:</strong> {item.poster_name}
                    </p>

                    {/* Example: show location if present */}
                    {loc && (
                      <p className="item-location">
                        <strong>Location:</strong> {loc?.name ?? `${loc.lat}, ${loc.lng}`}
                      </p>
                    )}

                    {/* Message button logic preserved */}
                    {userId !== item.user_id && (
                      <button
                        className="toggle-button"
                        onClick={() => navigate(`/messages/${item.id}/${item.user_id}`)}
                      >
                        Message Re-user
                      </button>
                    )}
                  </div>
                </li>
              );
            })}
          </ul>
        </div>
      </main>
    </div>
  );
}

export default ItemList;
