import React, { useEffect, useState } from 'react';
import '../styles/ItemList.css';
import { jwtDecode } from 'jwt-decode'; // ✅ Correct


const ItemList = () => {
  const [items, setItems] = useState([]);
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [exchangeCondition, setExchangeCondition] = useState('');
  const [location, setLocation] = useState('');
  const [image, setImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [userId, setUserId] = useState(null);
  const [message, setMessage] = useState({ type: '', text: '' });

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const decoded = jwtDecode(token);
        setUserId(decoded.id); // assuming your JWT includes `id`
      } catch (err) {
        console.error('Invalid token:', err);
      }
    }

    const fetchItems = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/items');
        const contentType = response.headers.get('content-type');

        if (response.ok && contentType.includes('application/json')) {
          const result = await response.json();
          setItems(result);
        } else {
          const text = await response.text();
          console.error('Server returned non-JSON:', text);
        }
      } catch (error) {
        console.error('Error fetching items:', error);
      }
    };

    fetchItems();
  }, []);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    } else {
      setImagePreview(null);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token || !userId) return;

    const formData = new FormData();
    formData.append('title', name);
    formData.append('description', description);
    formData.append('location', location);
    formData.append('image', image);
    formData.append('exchangeCondition', exchangeCondition);
    formData.append('userId', userId);

    try {
      const response = await fetch('http://localhost:5000/api/items', {
        method: 'POST',
        headers: {
          Authorization: `Bearer ${token}`,
        },
        body: formData,
      });

      const result = await response.json();

      if (response.ok) {
        setMessage({ type: 'success', text: result.message || 'Item posted successfully!' });
        setName('');
        setDescription('');
        setExchangeCondition('');
        setLocation('');
        setImage(null);
        setImagePreview(null);

        const refreshedItems = await fetch('http://localhost:5000/api/items');
        const itemsData = await refreshedItems.json();
        setItems(itemsData);
      } else {
        console.error('Server responded with:', result);
        setMessage({ type: 'error', text: result.message || 'Failed to post item. Please try again.' });
      }
    } catch (error) {
      console.error('Error posting item:', error);
      setMessage({ type: 'error', text: 'Network error. Please try again later.' });
    }
  };

  return (
    <div className="item-list-container">
      <h2>Available Items</h2>

      {message.text && (
        <div className={`message ${message.type}`}>
          {message.text}
        </div>
      )}

      {userId && (
        <form onSubmit={handleSubmit} className="item-form" encType="multipart/form-data">
          <input
            type="text"
            placeholder="Item Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
          <textarea
            placeholder="Item Description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
          />
          <input
            type="text"
            placeholder="Exchange Conditions"
            value={exchangeCondition}
            onChange={(e) => setExchangeCondition(e.target.value)}
            required
          />
          <input
            type="text"
            placeholder="Location"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
            required
          />
          <input
            type="file"
            accept="image/*"
            onChange={handleImageChange}
            required
          />
          {imagePreview && (
            <img src={imagePreview} alt="Preview" className="image-preview" />
          )}
          <button type="submit">Post Item</button>
        </form>
      )}

      <ul className="item-list">
        {items.map((item) => (
          <li key={item.id} className="item-card">
            <h3>{item.title}</h3>
            <p>{item.description}</p>
            <p><strong>Exchange Conditions:</strong> {item.exchangeCondition?.join(', ')}</p>
            <p><strong>Location:</strong> {item.location}</p>
            {item.image_url && <img src={item.image_url} alt={item.title} className="item-image" />}
          </li>
        ))}
      </ul>
    </div>
  );
};


export default ItemList;
