import React from 'react';
import { useNavigate } from 'react-router-dom';
import './ItemList.css';

function ItemList({ displayedItems, userId }) {
  const navigate = useNavigate();

  return (
    <ul className="item-list">
      {displayedItems.map((item, index) => {
        let loc = null;
        try {
          loc = JSON.parse(item.location);
        } catch (e) {
          loc = null;
        }

        return (
          <li key={`${item.id}-${index}`} className="item-card">
            <h3>{item.title}</h3>
            <p>{item.description}</p>
            <p><strong>Exchange Conditions:</strong> {item.exchange_condition}</p>
            <p><strong>Posted by:</strong> {item.poster_name}</p>
            <img
              src={`http://localhost:5000/api/items/${item.id}/image`}
              alt={item.title}
              className="item-image"
            />
            {loc && (
              <div className="item-map">
                <iframe
                  width="100%"
                  height="200"
                  frameBorder="0"
                  style={{ border: 0 }}
                  src={`https://www.google.com/maps?q=${loc.lat},${loc.lng}&hl=es;z=14&output=embed`}
                  allowFullScreen
                  title="Item Location"
                ></iframe>
              </div>
            )}
            {userId !== item.user_id && (
              <button
                className="messages-button"
                onClick={() => navigate(`/messages/${item.id}/${item.user_id}`)}
              >
                Message Re-user
              </button>
            )}
          </li>
        );
      })}
    </ul>
  );
}

export default ItemList;
