import React, { useEffect, useRef, useState } from 'react';

const MapView = () => {
  const mapRef = useRef(null);
  const [items, setItems] = useState([]);

  useEffect(() => {
    // Fetch items from the backend
    const fetchItems = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/items');
        const data = await response.json();
        setItems(data);
      } catch (error) {
        console.error('Error fetching items:', error);
      }
    };

    const initMap = () => {
      const defaultCenter = { lat: -25.7676, lng: 29.4648 }; // Middelburg
      const map = new window.google.maps.Map(mapRef.current, {
        center: defaultCenter,
        zoom: 12,
      });

      // Add markers for each item with a valid location
      items.forEach((item) => {
        try {
          const loc = JSON.parse(item.location);
          if (loc?.lat && loc?.lng) {
            new window.google.maps.Marker({
              position: { lat: loc.lat, lng: loc.lng },
              map,
              title: item.title,
            });
          }
        } catch (e) {
          console.warn('Invalid location data for item:', item.id);
        }
      });
    };

    const loadMapScript = () => {
      const existingScript = document.getElementById('googleMaps');

      if (!existingScript) {
        const script = document.createElement('script');
        script.src =
          'https://maps.googleapis.com/maps/api/js?key=AIzaSyCIho9p8rGILl7OsjimbOMjsEn0SaaxrQE';
        script.id = 'googleMaps';
        script.async = true;
        script.defer = true;
        script.onload = initMap;
        document.head.appendChild(script);
      } else {
        initMap();
      }
    };

    fetchItems().then(() => {
      if (!window.google || !window.google.maps) {
        loadMapScript();
      } else {
        initMap();
      }
    });
  }, [items]);

  return <div ref={mapRef} style={{ height: '100vh', width: '100%' }} />;
};

export default MapView;
