import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const Messages = ({ currentUserId }) => {
  const { itemId, receiverId } = useParams(); // assuming these are in the route
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  useEffect(() => {
    fetch(`/api/messages/${itemId}/${currentUserId}`)
      .then(res => res.json())
      .then(data => setMessages(data));
  }, [itemId, currentUserId]);

  const handleSend = async () => {
    if (newMessage.trim() === '') return;

    await fetch('/api/messages', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        senderId: currentUserId,
        receiverId,
        itemId,
        content: newMessage,
      }),
    });

    setMessages([...messages, {
      sender_id: currentUserId,
      receiver_id: receiverId,
      item_id: itemId,
      content: newMessage,
      timestamp: new Date().toISOString(),
    }]);
    setNewMessage('');
  };

  return (
    <div className="messages-container">
      <h2>Messages</h2>
      <div className="messages-list">
        {messages.map((msg, index) => (
          <div key={index} className={`message ${msg.sender_id === currentUserId ? 'sent' : 'received'}`}>
            <strong>{msg.sender_id === currentUserId ? 'You' : 'Them'}:</strong> {msg.content}
          </div>
        ))}
      </div>

      <div className="message-input">
        <input
          type="text"
          placeholder="Type your message..."
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
        />
        <button onClick={handleSend}>Send</button>
      </div>
    </div>
  );
};

export default Messages;
