import React from 'react';
import '../styles/EcoPoints.css';
import { useNavigate, Link } from 'react-router-dom';

const EcoPoints = () => {
  const pointsData = {
    totalPoints: 1200,
    breakdown: [
      { category: 'Plastic Bottles', points: 400 },
      { category: 'Cardboard Boxes', points: 300 },
      { category: 'Glass Jars', points: 250 },
      { category: 'Aluminum Cans', points: 250 },
    ],
  };

  return (
    <div className="eco-points-container">
      <h2>My Eco Points</h2>
      <div className="total-points">
        <h3>Total Points: {pointsData.totalPoints}</h3>
      </div>

      <div className="points-breakdown">
        <h4>Points Breakdown</h4>
        <ul>
          {pointsData.breakdown.map((item, index) => (
            <li key={index}>
              <span className="category">{item.category}</span>
              <span className="points">{item.points} pts</span>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default EcoPoints;
