import React, { useEffect, useState } from 'react';
import '../styles/EcoPoints.css';
import { useAuth } from '../Controller/authController'; // Adjust path as needed

const EcoPoints = () => {
  const [todayPoints, setTodayPoints] = useState(0);
  const [leaderboard, setLeaderboard] = useState([]);
  const { user } = useAuth(); // Get user from context

  useEffect(() => {
    if (!user?.fullName) return;

    // Fetch today's points
    fetch(`http://localhost:5000/api/todayPoints?name=${encodeURIComponent(user.fullName)}`)
      .then(res => res.json())
      .then(data => {
        if (data && typeof data.points === 'number') {
          setTodayPoints(data.points);
        }
      })
      .catch(err => console.error("Error fetching today's points:", err));

    // Fetch leaderboard
    fetch('http://localhost:5000/api/leaderboard')
      .then(res => res.json())
      .then(data => {
        if (Array.isArray(data)) {
          setLeaderboard(data);
        } else {
          console.error('Leaderboard data is not an array:', data);
        }
      })
      .catch(err => console.error('Error fetching leaderboard:', err));
  }, [user]);

  return (
    <div className="eco-container">
      <h1>Eco Points</h1>
      <p className="points">Points Earned Today: {todayPoints}</p>

      <div className="leaderboard">
        <h2>Leaderboard</h2>
        <ul>
          {leaderboard.map((entry, index) => (
            <li key={index}>
              #{index + 1} - {entry.full_name}: {entry.points} pts
              {entry.points >= 100 && <span> ⭐️</span>}
              {entry.full_name === user.fullName && <strong> (You)</strong>}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default EcoPoints;
