import React, { useEffect, useState } from 'react';
import '../styles/EcoPoints.css';

const EcoPoints = () => {
  const [todayPoints, setTodayPoints] = useState(0);
  const [leaderboard, setLeaderboard] = useState([]);
  const userName = 'You'; // Replace with actual logged-in user's name

  useEffect(() => {
    // Fetch today's points for the current user
    fetch(`http://localhost:5000/api/todayPoints?name=${userName}`)
      .then(res => res.json())
      .then(data => {
        if (data && typeof data.points === 'number') {
          setTodayPoints(data.points);
        }
      })
      .catch(err => console.error('Error fetching today\'s points:', err));

    // Fetch leaderboard
    fetch('http://localhost:5000/api/leaderboard')
      .then(res => res.json())
      .then(data => {
        if (Array.isArray(data)) {
          // Sort leaderboard by points descending
          const sorted = data.sort((a, b) => b.points - a.points);
          setLeaderboard(sorted);
        } else {
          console.error('Leaderboard data is not an array:', data);
        }
      })
      .catch(err => console.error('Error fetching leaderboard:', err));
  }, []);

  return (
    <div className="eco-container">
      <h1>Eco Points</h1>
      <p className="points">Points Earned Today: {todayPoints}</p>

      <div className="leaderboard">
        <h2>Leaderboard</h2>
        <ul>
          {leaderboard.map((user, index) => (
            <li key={index}>
              #{index + 1} - {user.full_name}: {user.points} pts
              {user.points >= 100 && <span> ⭐️</span>}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default EcoPoints;
