import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';

import Analysis from './components/Analysis';
import ManageUsers from './components/ManageUsers';

import ModerateItemsExchange from './components/ModerateItemsExchange';
import ExchangeTracking from './components/ExchangeHistory';
import ProtectedRoute from './components/ProtectedRoute';
import NavbarAdmin from './components/NavbarAdmin';

function AdminApp() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);

  useEffect(() => {
    const checkAuth = () => {
      const token = localStorage.getItem('token');
      if (token === 'admin-token') {
        setIsAuthenticated(true);
        setIsAdmin(true);
      } else if (token && token.split('.').length === 3) {
        try {
          const decoded = jwtDecode(token);
          setIsAuthenticated(true);
          setIsAdmin(decoded.role === 'admin'); // assuming token has a 'role' field
        } catch (err) {
          console.error('Invalid token:', err);
          setIsAuthenticated(false);
        }
      } else {
        setIsAuthenticated(false);
      }
    };

    checkAuth();
    window.addEventListener('storage', checkAuth);

    return () => {
      window.removeEventListener('storage', checkAuth);
    };
  }, []);

  return (
    <Router>
      {(isAuthenticated && isAdmin) && <NavbarAdmin />}
      <Routes>
        <Route path="/" element={isAuthenticated ? <Navigate to="/analysis" /> : <Login />} />
        <Route path="/login" element={isAuthenticated ? <Navigate to="/analysis" /> : <Login />} />
        <Route
          path="/analysis"
          element={
            <ProtectedRoute isAuthenticated={isAuthenticated && isAdmin}>
              <Analysis />
            </ProtectedRoute>
          }
        />
        <Route
          path="/ManageUsers"
          element={
            <ProtectedRoute isAuthenticated={isAuthenticated && isAdmin}>
              <ManageUsers />
            </ProtectedRoute>
          }
        />
        
        <Route
          path="/ExchangeHistory"
          element={
            <ProtectedRoute isAuthenticated={isAuthenticated && isAdmin}>
              <ExchangeTracking />
            </ProtectedRoute>
          }
        />
        <Route
          path="*"
          element={<Navigate to={isAuthenticated && isAdmin ? "/aAnalysis" : "/login"} />}
        />
      </Routes>
    </Router>
  );
}

export default AdminApp;
