import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import MapPicker from './MapPicker';
import '../styles/ItemList.css';

const ItemList = () => {
  const [items, setItems] = useState([]);
  const [recommendations, setRecommendations] = useState([]);
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [exchangeCondition, setExchangeCondition] = useState('');
  const [category, setCategory] = useState('electronics'); // New state
  const [location, setLocation] = useState(null);
  const [image, setImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [userId, setUserId] = useState(null);
  const [messages, setMessages] = useState({ type: '', text: '' });
  const [showRecentOnly, setShowRecentOnly] = useState(false);

  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      if (token.split('.').length === 3) {
        try {
          const decoded = jwtDecode(token);
          setUserId(decoded.id);
          fetchRecommendations(decoded.id);
        } catch (err) {
          console.error('Invalid token:', err);
        }
      } else if (token === 'admin-token') {
        setUserId('admin');
      }
    }

    fetchItems();
  }, []);

  const fetchItems = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/items');
      const result = await response.json();
      setItems(result);
    } catch (error) {
      console.error('Error fetching items:', error);
    }
  };

  const fetchRecommendations = async (userId) => {
    try {
      const response = await fetch(`http://localhost:5000/recommend/${userId}`);
      const result = await response.json();
      setRecommendations(result.items || []);
    } catch (error) {
      console.error('Error fetching recommendations:', error);
    }
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    } else {
      setImagePreview(null);
    }
  };

  const enhanceLocation = async (location) => {
    try {
      const response = await fetch('http://localhost:5000/nearby', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(location),
      });
      const data = await response.json();
      return data.items;
    } catch (err) {
      console.error('Location enhancement failed:', err);
      return [];
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token || !userId || !location) return;

    const nearbyItems = await enhanceLocation(location);

    const formData = new FormData();
    formData.append('title', name);
    formData.append('description', description);
    formData.append('location', JSON.stringify(location));
    formData.append('image', image);
    formData.append('exchangeCondition', exchangeCondition);
    formData.append('category', category); // Use selected category

    try {
      const response = await fetch('http://localhost:5000/api/items', {
        method: 'POST',
        headers: {
          Authorization: `Bearer ${token}`,
        },
        body: formData,
      });

      const result = await response.json();

      if (response.ok) {
        setMessages({ type: 'success', text: result.message || 'Item posted successfully!' });
        setName('');
        setDescription('');
        setExchangeCondition('');
        setCategory('electronics');
        setLocation(null);
        setImage(null);
        setImagePreview(null);
        fetchItems();
      } else {
        setMessages({ type: 'error', text: result.message || 'Failed to post item.' });
      }
    } catch (error) {
      setMessages({ type: 'error', text: 'Network error. Please try again later.' });
    }
  };

  const toggleView = () => {
    setShowRecentOnly(!showRecentOnly);
  };

  const displayedItems = showRecentOnly ? items.slice(0, 5) : items;

  return (
    <div className="item-list-container">
      <h2>Available Items</h2>

      {messages.text && (
        <div className={`messages ${messages.type}`}>
          {messages.text}
        </div>
      )}

      {userId && (
        <form onSubmit={handleSubmit} className="item-form" encType="multipart/form-data">
          <input
            type="text"
            placeholder="Item Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
          <textarea
            placeholder="Item Description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
          />
          <input
            type="text"
            placeholder="Exchange Conditions"
            value={exchangeCondition}
            onChange={(e) => setExchangeCondition(e.target.value)}
            required
          />
          <select value={category} onChange={(e) => setCategory(e.target.value)} required>
            <option value="electronics">Electronics</option>
            <option value="clothes">Clothes</option>
            <option value="textbook">Textbook</option>
          </select>
          <label>Select Location on Map:</label>
          <MapPicker setLocation={setLocation} />
          <input
            type="file"
            accept="image/*"
            onChange={handleImageChange}
            required
          />
          {imagePreview && (
            <img src={imagePreview} alt="Preview" className="image-preview" />
          )}
          <button type="submit">Post Item</button>
        </form>
      )}

      <button onClick={toggleView} className="toggle-button">
        {showRecentOnly ? 'Show All Items' : 'Show Recent Items'}
      </button>

      <ul className="item-list">
        {displayedItems.map((item) => {
          let loc = null;
          try {
            loc = JSON.parse(item.location);
          } catch (e) {
            loc = null;
          }

          return (
            <li key={item.id} className="item-card">
              <h3>{item.title}</h3>
              <p>{item.description}</p>
              <p><strong>Exchange Conditions:</strong> {item.exchange_condition}</p>
              <p><strong>Category:</strong> {item.category}</p>
              <p><strong>Posted by:</strong> {item.poster_name}</p>
              <img
                src={`http://localhost:5000/api/items/${item.id}/image`}
                alt={item.title}
                className="item-image"
              />
              {loc && (
                <div className="item-map">
                  <iframe
                    width="100%"
                    height="200"
                    frameBorder="0"
                    style={{ border: 0 }}
                    src={`https://www.google.com/maps?q=${loc.lat},${loc.lng}&hl=es;z=14&output=embed`}
                    allowFullScreen
                    title="Item Location"
                  ></iframe>
                </div>
              )}
              {userId !== item.user_id && (
                <button
                  className="messages-button"
                  onClick={() => navigate(`/messages/${item.id}/${item.user_id}`)}
                >
                  Message Re-user
                </button>
              )}
            </li>
          );
        })}
      </ul>

      {recommendations.length > 0 && (
        <div className="recommendations">
          <h3>Recommended for You</h3>
          <ul>
            {recommendations.map((itemId) => (
              <li key={itemId}>Item ID: {itemId}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default ItemList;
