import React, { useEffect, useState } from 'react';
import '../styles/EcoPoints.css';

const EcoPoints = () => {
  const [points, setPoints] = useState(0);
  const [message, setMessage] = useState('');
  const [leaderboard, setLeaderboard] = useState([]);
  const userName = 'You';

  useEffect(() => {
    fetch('http://localhost:5000/api/points')
      .then(res => res.json())
      .then(data => setPoints(data.points));

   fetch('http://localhost:5000/api/leaderboard')
  .then(res => res.json())
  .then(data => {
    if (Array.isArray(data)) {
      setLeaderboard(data);
    } else {
      console.error('Leaderboard data is not an array:', data);
      setLeaderboard([]);
    }
  });
  }, []);

  const handleAction = (action) => {
    let reward = 0;
    switch (action) {
      case 'recycle': reward = 10; break;
      case 'bike': reward = 15; break;
      case 'plant': reward = 20; break;
      default: reward = 0;
    }
    const newPoints = points + reward;
    setPoints(newPoints);
    setMessage(`You earned ${reward} Eco Points for ${action}!`);

    fetch('http://localhost:5000/api/updatePoints', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ name: userName, points: reward })
    }).then(() => {
      fetch('http://localhost:5000/api/leaderboard')
        .then(res => res.json())
        .then(data => setLeaderboard(data));
    });
  };

  return (
    <div className="eco-container">
      <h1>Eco Points</h1>
      <p className="points">Your Eco Points: {points}</p>
      <div className="actions">
        <button onClick={() => handleAction('recycle')}>Recycle</button>
        <button onClick={() => handleAction('bike')}>Bike Instead of Drive</button>
        <button onClick={() => handleAction('plant')}>Plant a Tree</button>
      </div>
      {message && <p className="message">{message}</p>}

      <div className="leaderboard">
        <h2>Leaderboard</h2>
        <ul>
          {leaderboard.map((user, index) => (
            <li key={index}>{user.name}: {user.points} pts</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default EcoPoints;
