import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import MapPicker from './MapPicker';
import '../styles/ItemList.css';

const ItemList = () => {
  const [items, setItems] = useState([]);
  const [recommendations, setRecommendations] = useState([]);
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [exchangeCondition, setExchangeCondition] = useState('');
  const [category, setCategory] = useState('');
  const [location, setLocation] = useState(null);
  const [image, setImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [userId, setUserId] = useState(null);
  const [identifier, setIdentifier] = useState('');
  const [messages, setMessages] = useState({ type: '', text: '' });
  const [showRecentOnly, setShowRecentOnly] = useState(false);

  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token && token.split('.').length === 3) {
      try {
        const decoded = jwtDecode(token);
        setUserId(decoded.id);
        setIdentifier(decoded.email);
        fetchRecommendations(decoded.email);
      } catch (err) {
        console.error('Invalid token:', err);
      }
    }
    fetchItems();
  }, []);

  const fetchItems = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/items');
      const result = await response.json();
      setItems(result);
    } catch (error) {
      console.error('Error fetching items:', error);
    }
  };

  const fetchRecommendations = async (email) => {
    try {
      const response = await fetch(`http://localhost:5000/recommend?identifier=${email}`);
      const result = await response.json();
      setRecommendations(result.items || []);
    } catch (error) {
      console.error('Error fetching recommendations:', error);
    }
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    } else {
      setImagePreview(null);
    }
  };

  const categorizeItem = async (desc) => {
    try {
      const response = await fetch('http://localhost:5000/categorize', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ description: desc })
      });
      const result = await response.json();
      return result.category;
    } catch (error) {
      console.error('Error categorizing item:', error);
      return 'unknown';
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token || !userId || !location) return;

    const predictedCategory = await categorizeItem(description);

    const formDataToSend = new FormData();
    formDataToSend.append('title', name);
    formDataToSend.append('description', description);
    formDataToSend.append('location', JSON.stringify(location));
    formDataToSend.append('image', image);
    formDataToSend.append('exchangeCondition', exchangeCondition);
    formDataToSend.append('category', predictedCategory);

    try {
      const response = await fetch('http://localhost:5000/api/items', {
        method: 'POST',
        headers: {
          Authorization: `Bearer ${token}`,
        },
        body: formDataToSend,
      });

      const result = await response.json();

      if (response.ok) {
        await fetch('http://localhost:5000/log_item', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            user_id: userId,
            item_id: result.item_id,
            category: predictedCategory,
            location,
            description,
            title: name,
            exchange_condition: exchangeCondition,
            poster_name: identifier
          }),
        });

        setMessages({ type: 'success', text: result.message || 'Item posted successfully!' });
        setName('');
        setDescription('');
        setExchangeCondition('');
        setCategory('');
        setLocation(null);
        setImage(null);
        setImagePreview(null);
        fetchItems();
      } else {
        setMessages({ type: 'error', text: result.message || 'Failed to post item.' });
      }
    } catch (error) {
      setMessages({ type: 'error', text: 'Network error. Please try again later.' });
    }
  };

  const toggleView = () => {
    setShowRecentOnly(!showRecentOnly);
  };

  const displayedItems = showRecentOnly ? items.slice(0, 5) : items;

  return (
    <div className="item-list-container">
      <h2>Available Items</h2>

      {messages.text && (
        <div className={`messages ${messages.type}`}>{messages.text}</div>
      )}

      {userId && (
        <form onSubmit={handleSubmit} className="item-form" encType="multipart/form-data">
          <input type="text" placeholder="Item Name" value={name} onChange={(e) => setName(e.target.value)} required />
          <textarea placeholder="Item Description" value={description} onChange={(e) => setDescription(e.target.value)} required />
          <input type="text" placeholder="Exchange Conditions" value={exchangeCondition} onChange={(e) => setExchangeCondition(e.target.value)} required />
          <label>Select Location on Map:</label>
          <MapPicker setLocation={setLocation} />
          <input type="file" accept="image/*" onChange={handleImageChange} required />
          {imagePreview && <img src={imagePreview} alt="Preview" className="image-preview" />}
          <button type="submit">Post Item</button>
        </form>
      )}

      <button onClick={toggleView} className="toggle-button">
        {showRecentOnly ? 'Show All Items' : 'Show Recent Items'}
      </button>

      <ul className="item-list">
        {displayedItems.map((item) => (
          <li key={item.id} className="item-card">
            <h3>{item.title}</h3>
            <p>{item.description}</p>
            <p><strong>Exchange Conditions:</strong> {item.exchange_condition}</p>
            <p><strong>Category:</strong> {item.category}</p>
            <p><strong>Posted by:</strong> {item.poster_name}</p>
            <img src={`http://localhost:5000/api/items/${item.id}/image`} alt={item.title} className="item-image" />
          </li>
        ))}
      </ul>

      {recommendations.length > 0 && (
        <div className="recommendations">
          <h3>Recommended for You</h3>
          <ul className="item-list">
            {recommendations.map((item) => (
              <li key={item.item_id} className="item-card">
                <h4>{item.title}</h4>
                <p>{item.description}</p>
                <p><strong>Exchange Conditions:</strong> {item.exchange_condition}</p>
                <p><strong>Category:</strong> {item.category}</p>
                <p><strong>Posted by:</strong> {item.poster_name}</p>
                <img src={`http://localhost:5000/api/items/${item.item_id}/image`} alt={item.title} className="item-image" />
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default ItemList;
